<!DOCTYPE html><html><head><meta http-equiv="Pragma" content="no-cache"><meta http-equiv="Cache-Control" content="no-cache"><meta charset="utf-8"><title>PHP MySQL TEST</title></head><body><?php// エラーを画面に表示するini_set("display_errors", 1);error_reporting(E_ALL);echo '<h3>'."MySQLへの接続開始".'</h3>';// HOST NAMEを入れる$hostname = "us-cdbr-iron-east-03.cleardb.net";$port = "3306";// DB nameをいれる$dbname = "ad_3c1d7d0f7152457";$dsn = "mysql:host=$hostname;port=$port;dbname=$dbname;charset=utf8";echo "⇒ 接続DSN＝$dsn".'<br>';// ユーザーnameエラーををいれる$username = "b759d8d73f22bb";// pwdをいれる$password = "26213cf9";try {    $pdo = new PDO($dsn, $username, $password,    [        PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,        PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,    ] );    echo "MySQL接続成功".'<br>';    echo nl2br("\n");        // SQL文    $sql = "SELECT empid, name, shozoku, address FROM personal";    echo "SQL文：$sql".'<br>';    // SQL実行    $result = $pdo->query($sql);    echo "⇒ SQL文を実行しました".'<br>';    // 取得したレコード数    $count = $result->rowCount();    // 結果を表示    echo '<h3>'."============== 結果 ==============".'</h3>';    echo "取得したレコード数＝$count".'<br>';    echo '<p>';    foreach ($result as $row) {        echo ' empid = '.$row['empid']."  ";        echo ' name = '.$row['name']."  ";        echo ' shozoku = '.$row['shozoku']."  ";        echo ' address = '.$row['address'];        echo nl2br("\n");    }    echo '</p>';    // 接続完了    $result = null;    $dsn = null;// 例外発生時} catch (PDOException $e) {    echo '<h3>'."MySQLデータベースへの接続に失敗しました".'</h3>';    exit('接続失敗。'.$e->getMessage());}?></body></html>